import React, { useState } from 'react'
import "./dashboard.css"
import empData from "../../assets/data/employeesData.json"
import { Card, Image, Modal } from 'semantic-ui-react'

const Dashboard = () => {
  const [open, setOpen] = useState(false);
  const [selectedEmp, setSelectedEmp] = useState(null);

  const clickHandler = (emp) => {
    setOpen(!open);
    if (selectedEmp) {
      setSelectedEmp(null);
    } else {
      setSelectedEmp(emp);
    }

  }
  console.log(selectedEmp)

  return (
    <div className='dashboard-container'>
      <div className='sidebar'>

      </div>
      <div className='main'>
        { empData.employees.map((emp, index) => (
          <Card key={index} className='card'onClick={() => {clickHandler(emp)}} >
            <Image src={ require("../../assets/images/profile-icon.jpg") } ui={ false } style={ { width: "100px", height: "100px" } } />
            <Card.Content className='card-content'>
              <Card.Header>{emp.fullName}</Card.Header>
              <Card.Meta>Joined in {emp.doj}</Card.Meta>
              <Card.Description>{emp.role}</Card.Description>
            </Card.Content>
          </Card>
        )) }
        {open && (
          <Modal>
            <Modal.Content image>
              <Image size='medium' src={require("../../assets/images/profile-icon.jpg")} />
            </Modal.Content>
          </Modal>
        )}
      </div>
    </div>
  )
}

export default Dashboard
