import React, { useState } from 'react'
import { Grid, Item, Label } from 'semantic-ui-react'
import SideBar from '../../components/SideBar/SideBar'
import EmployeeModal from '../../components/EmployeeModal/EmployeeModal';
import projectData from "../../assets/data/projectDetails.json"
import empData from "../../assets/data/employeesData.json"

const Projects = () => {

  const [open, setOpen] = useState(false);
  const [selectedEmp, setSelectedEmp] = useState(open || "");

  const developers = (teamMembers) => {
    let arr = [];
    empData.employees.forEach((emp, index) => {
      // console.log(emp)
      if (teamMembers.includes(emp.empID)) {
        console.log(emp.empID)
        arr.push(emp);
      }
    })
    console.log("developers:", arr);

    const filteredEmployees = empData.employees.filter((emp) =>
    teamMembers.includes(emp.empID)
  );

  console.log("Developers for Project:", filteredEmployees);
  }

  const clickHandler = (emp) => {
    setOpen(!open);
    setSelectedEmp(emp);
    <EmployeeModal selectedEmp={ selectedEmp } open={ open } setOpen={ setOpen } />
  }

  return (
    <div>
      <Grid style={ { width: "100vw" } }>
        <Grid.Column style={ { width: "12%" } }>
          <SideBar />
        </Grid.Column>
        <Grid.Column style={ { width: "88%", padding: "20px" } }>
          <Item.Group divided >

            { projectData.map((project, index) => (
              <Item key={ index }>
                <Item.Image size='small' src={ (project.src) } />
                <Item.Content style={ { padding: "20px" } }>
                  <Item.Header>{ project.title }</Item.Header>
                  <Item.Meta>
                    { project.projectTeam }
                  </Item.Meta>
                  <Item.Description>
                    { project.description }
                  </Item.Description>
                  <Item.Extra>
                    { project.techStack }
                    {developers(project.teamMembers)}
                    <Label as='a' color='blue' image>

                      Veronika
                      <Label.Detail>Friend</Label.Detail>
                    </Label>
                  </Item.Extra>
                </Item.Content>
              </Item>
            )) }

          </Item.Group>
        </Grid.Column>
      </Grid>
    </div>
  )
}

export default Projects
