import React, { useEffect, useState } from 'react'
import { Dropdown, Input } from 'semantic-ui-react'
import empData from "../../assets/data/employeesData.json"
import { useFormik } from 'formik'
import "./employeeFilter.css"

const filterBy = [
  { key: "role", text: "By Role", value: "role" },
  { key: "fullName", text: "By Name", value: "fullName" }
]

const sortBy = [
  { key: "doj", text: "By DOJ", value: "doj" },
  { key: "exp", text: "By Exp", value: "exp" }
]

const EmployeeFilter = (props) => {
  const formik = useFormik({
    initialValues: {
      selectedFilter: "",
      select
    }
  });

  return (
    <div className='filter-bar'>
      <Input
        action={
          <Dropdown
            button basic floating placeholder='Filter By' options={ filterBy } defaultValue='role' />
        }
        icon='search'
        iconPosition='left'
        placeholder="Search here"
      />
      <Dropdown 
        button basic floating placeholder='Sort By' options={ sortBy } defaultValue="exp" />
    </div>
  )
}

export default EmployeeFilter
