import React, { useState } from 'react'
import { Button, Card, Header, Icon, Image, Modal } from 'semantic-ui-react'
import empData from "../../assets/data/employeesData.json"
import "./dashboard.css"

const Dashboard = () => {
  const [open, setOpen] = useState(false);
  const [selectedEmp, setSelectedEmp] = useState(open || "");

  const clickHandler = (emp) => {
    setOpen(!open);
    // if (selectedEmp) {
    //   setSelectedEmp(null);
    // } else {
    setSelectedEmp(emp);
    // }

  }
  console.log("open:", open)
  console.log("selectedEmp:", selectedEmp);

  return (
    <div className='dashboard-container'>
      <div className='sidebar'>
        <Icon name={"building"} size='big'></Icon>
      </div>
      <div className='main'>

        <Modal open={ open }>
          <Modal.Content image>
            <Image size='medium' src={ require("../../assets/images/profile-icon.jpg") } wrapped />
            <Modal.Description>
              <Header>{ selectedEmp.fullName }</Header>
              { Object.entries(selectedEmp).map((data, value) => (
                (data[0] !== "fullName" && data[0] !== "password" && (
                  <p key={data[0]}><span>{ data[0] } : </span>{ data[1] }</p>
                ))
              )) }
            </Modal.Description>
          </Modal.Content>
          <Modal.Actions>
            <Button primary onClick={ () => setOpen(false) }>
              Close
            </Button>
          </Modal.Actions>
        </Modal>
      </div>
    </div>
  )
}

export default Dashboard
