import React, { useEffect, useState } from 'react'
import { Dropdown, Input } from 'semantic-ui-react'
import empData from "../../assets/data/employeesData.json"
import "./employeeFilter.css"

const filterBy = [
  { key: "role", text: "By Role", value: "role" },
  { key: "fullName", text: "By Name", value: "fullName" }
]

const sortBy = [
  { key: "doj", text: "By DOJ", value: "doj" },
  { key: "exp", text: "By Exp", value: "exp" }
]

const EmployeeFilter = (props) => {
  const [filterOption, setFilterOption] = useState("role" || "");
  const [sortOption, setSortOption] = useState("");
  const [input, setInput] = useState("");
  const [selectedEmp, setSelectedEmp] = useState([]);
  const [validSearch, setValidSearch] = useState(true);


  const searchHandler = (e) => {
    setInput(e.target.value);
    console.log("set input:", input)
  }

  const filterHandler = (e) => {
    setFilterOption(e.target.textContent);
    console.log(e.target.textContent)
    console.log(fik)
  }

  const sortHandler = (e, { value }) => {
    setSortOption(value)
    // let sort_name = e.target.textContent;
    // console.log(sort_name);
  }

  useEffect(()=> {
    let temp = empData.employees.filter((emp, input) => emp);
    let data = [];

    if (input) {
      temp.map((emp, index) => {
        if (emp[filterOption].toLowerCase().replace(" ", "").includes(input.toLowerCase().replace(" ", ""))) {
          // console.log(emp["fullName"], emp[filterOption]);
          data.push(emp);
          setValidSearch(true);
        }
      })

    } 

    if (data.length > 0 && input.length !== 0) {
      console.log("gt", input)
      props.setFilteredEmp(data);
    } else {
      console.log("lt", input)
      props.setFilteredEmp(temp);
    }

    if(input.length === 0) {
      console.log("zero", input)
      props.setFilteredEmp(temp);
    }
  }, [input, filterOption, sortOption]);


  return (
    <div className='filter-bar'>
      <Input
        action={
          <Dropdown
            button basic floating placeholder='Filter By' options={ filterBy } defaultValue='role' onChange={ (e) => {filterHandler(e);} } />
        }
        icon='search'
        iconPosition='left'
        value={ input }
        onChange={(e) => {searchHandler(e);} }
        placeholder="Search here"
      />
      <Dropdown 
        button basic floating placeholder='Sort By' options={ sortBy } defaultValue="exp" onChange={ (e) => {sortHandler(e); }} />
    </div>
  )
}

export default EmployeeFilter
