import React, { useState } from 'react'
import "./dashboard.css"
import empData from "../../assets/data/employeesData.json"
import { Button, Card, Grid, GridColumn, Header, Image, Modal } from 'semantic-ui-react'

const Dashboard = () => {
  const [open, setOpen] = useState(false);
  const [selectedEmp, setSelectedEmp] = useState(open || "");

  const clickHandler = (emp) => {
    setOpen(!open);
    // if (selectedEmp) {
    //   setSelectedEmp(null);
    // } else {
    setSelectedEmp(emp);
    // }

  }
  console.log("open:", open)
  console.log("selectedEmp:", selectedEmp);

  return (
    <div className='dashboard-container'>
      <Grid>
        <GridColumn width={ 5 }>
          <div className='sidebar'>

          </div>
        </GridColumn>
        <GridColumn width={ 11 }>
          <div className='main'>
            { empData.employees.map((emp, index) => (
              <Card key={ index } className='card' onClick={ () => { clickHandler(emp) } } >
                <Image src={ require("../../assets/images/profile-icon.jpg") } ui={ false } style={ { width: "100px", height: "100px" } } />
                <Card.Content className='card-content'>
                  <Card.Header>{ emp.fullName }</Card.Header>
                  <Card.Meta>Joined in { emp.doj }</Card.Meta>
                  <Card.Description>{ emp.role }</Card.Description>
                </Card.Content>
              </Card>
            )) }
            <Modal open={ open }>
              <Modal.Content image>
                <Image size='medium' src={ require("../../assets/images/profile-icon.jpg") } wrapped />
                <Modal.Description>
                  <Header>{ selectedEmp.fullName }</Header>
                  { Object.entries(selectedEmp).map((data, value) => (
                    (data[0] !== "fullName" && (

                      <p><span>{ data[0] } : </span>{ data[1] }</p>
                    ))
                  )) }
                </Modal.Description>
              </Modal.Content>
              <Modal.Actions>
                <Button primary onClick={ () => setOpen(false) }>
                  Close
                </Button>
              </Modal.Actions>
            </Modal>
          </div>
        </GridColumn>
      </Grid>
    </div>
  )
}

export default Dashboard
