import React, { useState } from 'react'
import { Dropdown, Input } from 'semantic-ui-react'
import empData from "../../assets/data/employeesData.json"
import "./employeeFilter.css"

const filterBy = [
  { key: "role", text: "By Role", value: "role" },
  { key: "fullName", text: "By Name", value: "fullName" }
]

const sortBy = [
  { key: "doj", text: "By DOJ", value: "doj" },
  { key: "exp", text: "By Exp", value: "exp" }
]

const EmployeeFilter = (props) => {
  const [filterOption, setFilterOption] = useState("role");
  const [sortOption, setSortOption] = useState("");
  const [input, setInput] = useState("");
  const [selectedEmp, setSelectedEmp] = useState([]);
  const [validSearch, setValidSearch] = useState(true);


  const searchHandler = (e) => {
    setInput(e.target.value);
    prepareData();
  }

  const filterHandler = (e, { value }) => {
    setFilterOption(value);
    prepareData();
  }

  const sortHandler = (e, { value }) => {
    setSortOption(value)
    // let sort_name = e.target.textContent;
    // console.log(sort_name);
  }


  const prepareData = () => {
    let temp = empData.employees.filter((emp, input) => emp);
    let data = [];

    if (input) {
      temp.map((emp, index) => {
        if (emp[filterOption].toLowerCase().replace(" ", "").includes(input.toLowerCase().replace(" ", ""))) {
          // console.log(emp["fullName"], emp[filterOption]);
          data.push(emp);
          setValidSearch(true);
        }
      })

    } else {
      console.log("no input")
      props.setFilteredEmp(empData.employees)
    }

    if(data.length === 0) {
      setValidSearch(false);
    }
    // console.log(data);
    setSelectedEmp(data);
    if (validSearch) {
      props.setFilteredEmp(data);
      console.log(validSearch, "1")
      console.log(props.filteredEmp);
    } else {
      console.log(validSearch, "2")
      console.log(props.filteredEmp);
    }

  }


  return (
    <div className='filter-bar'>
      <Input
        action={
          <Dropdown
            button basic floating placeholder='Filter By' options={ filterBy } defaultValue='role' onChange={ filterHandler } />
        }
        icon='search'
        iconPosition='left'
        value={ input }
        onChange={ searchHandler }
        placeholder="Search here"
      />
      <Dropdown placeholder='Sort By' button basic floating options={ sortBy } onChange={ sortHandler } />
    </div>
  )
}

export default EmployeeFilter
