          <SideBar />
        </Grid.Column>
        <Grid.Column style={ { width: "88%", padding: "20px" } }>
          <Item.Group divided >

            { projectData.map((project, index) => (
              <Item key={ index }>
                <Item.Image size='small' src={ (project.src) } />
                <Item.Content style={ { padding: "20px" } }>
                  <Item.Header>{ project.title }</Item.Header>
                  <Item.Meta>
                    { project.projectTeam }
